nfo:\"Far northeast (Sambava, vanilla).\" ,\r\n  \r\n      area: 25518,\r\n      population: \"(estimate)\",\r\n      description: \"Northeast tropical region; famous for vanilla production, tropical forests, agriculture and coastal areas.\"},\r\n  { key: \"Androy\", title: \"Androy\", lat:-25.1667, lon:45.3833, info:\"Far south (Ambovombe).\",\r\n  \r\n      area: 19317,\r\n      population: 903376,\r\n     description: \"Extreme south region; arid climate, challenging agriculture, rural communities, semi-arid plains.\",\r\n     },\r\n  { key: \"Anosy\", title: \"Anosy\", lat:-24.52, lon:46.28, info:\"Southeast (Fort-Dauphin / Tolagnaro).\" ,\r\n  \r\n      area: 25731,\r\n      population: \"(estimate)\",\r\n      description: \"Southeast coastal region; coastal and littoral zones, fishing, forests and rural populations.\"}\r\n];\r\n\r\n/* ---------- Six main city markers ---------- */\r\nconst cityMarkers = [\r\n  { id: \"antananarivo\", title:\"Antananarivo\", lat:-18.8792, lon:47.5079, info:\"Capital city — Antananarivo.\" },\r\n  { id: \"toamasina\", title:\"Toamasina\", lat:-18.1492, lon:49.4023, info:\"Major port — Toamasina.\" },\r\n  { id: \"mahajanga\", title:\"Mahajanga\", lat:-15.7167, lon:46.3167, info:\"Coastal city — Mahajanga.\" },\r\n  { id: \"fianarantsoa\", title:\"Fianarantsoa\", lat:-21.4522, lon:47.0857, info:\"Highlands city — Fianarantsoa.\" },\r\n  { id: \"toliara\", title:\"Toliara\", lat:-23.3515, lon:43.6859, info:\"Southwest port — Toliara.\" },\r\n  { id: \"antsiranana\", title:\"Antsiranana\", lat:-12.2800, lon:49.2917, info:\"Northern port — Antsiranana.\" }\r\n];\r\n\r\n/* ---------- Colors palette ---------- */\r\nconst palette = [\r\n  \"#FF8A65\",\"#4FC3F7\",\"#81C784\",\"#BA68C8\",\"#FFD54F\",\r\n  \"#4DB6AC\",\"#FFB74D\",\"#90A4AE\",\"#A1887F\",\"#AED581\",\r\n  \"#4DB6E8\",\"#F06292\",\"#9575CD\",\"#F4B400\",\"#64B5F6\",\r\n  \"#E57373\",\"#BAE4B3\",\"#C5E1A5\",\"#B39DDB\",\"#FFCC80\",\r\n  \"#80CBC4\",\"#F8BBD0\",\"#D7CCC8\"\r\n];\r\n\r\n/* ---------- Create map ---------- */\r\nvar map = AmCharts.makeChart(\"mapdiv\", {\r\n  type: \"map\",\r\n  theme: \"light\",\r\n  projection: \"mercator\",\r\n\r\n  dataProvider: {\r\n    map: \"madagascarHigh\",\r\n    getAreasFromMap: true,\r\n    areas: [], // we\'ll augment these on init to add colors & customInfo\r\n    images: [] // we\'ll push labels and markers with customInfo\r\n  },\r\n\r\n  areasSettings: {\r\n    autoZoom: true,\r\n    selectable: true,\r\n    outlineColor: \"#555\",\r\n    rollOverOutlineColor: \"#222\",\r\n    rollOverColor: \"#67B7DC\",\r\n    balloonText: \"<b>[[title]]</b>\"\r\n  },\r\n\r\n  imagesSettings: {\r\n    selectable: true,\r\n    labelRollOverColor: \"#000\",\r\n    labelFontSize: 13,\r\n    labelColor: \"#111\",\r\n    balloonText: \"<b>[[title]]</b>\"\r\n  },\r\n\r\n  zoomControl: { zoomControlEnabled: true }\r\n});\r\n\r\n/* ---------- On init: inject customInfo into areas and populate images ---------- */\r\nmap.addListener(\"init\", function() {\r\n  // 1) Ensure areas exist\r\n  const areas = map.dataProvider.areas || [];\r\n\r\n  // Attempt to match area to our regions array by title or id (case-insensitive)\r\n  areas.forEach((area, idx) => {\r\n    const areaTitle = (area.title || area.id || \"\").toString().toLowerCase();\r\n    // find region whose title or key is included in areaTitle or vice-versa\r\n    let found = regions.find(r => {\r\n      const k = (r.title || r.key || \"\").toString().toLowerCase();\r\n      return areaTitle === k || areaTitle.indexOf(k) !== -1 || k.indexOf(areaTitle) !== -1;\r\n    });\r\n    if(!found) {\r\n      // fallback: try fuzzy includes for known words\r\n      found = regions.find(r => areaTitle.includes((r.title||\"\").split(\" \")[0].toLowerCase()));\r\n    }\r\n    // Assign color and customInfo\r\n    area.color = palette[idx % palette.length];\r\n    area.outlineColor = \"#333\";\r\n    area.customInfo = (found ? found.description : (area.description || area.title || \"No info available\"));\r\n    // if found, ensure title is consistent\r\n    if(found && !area.title) area.title = found.title;\r\n  });\r\n\r\n  // 2) Add label-images for each region centroid (these images will have customInfo)\r\n  \r\n    const svg = \"M9,0C4.029,0,0,4.029,0,9c0,4.971,4.029,9,9,9s9-4.029,9-9C18,4.029,13.971,0,9,0z\";\r\n  regions.forEach((r, i) => {\r\n    map.dataProvider.images.push({\r\n      id: \"lbl_\" + r.key,\r\n      latitude: r.lat,\r\n      longitude: r.lon,\r\n      label: r.title,\r\n      labelColor: \"#0b2540\",\r\n      labelFontSize: 13,\r\n      title: r.title,\r\n      svgPath: svg,\r\n      customInfo: r.info,\r\n      svgPath: \"\", // no icon, label-only\r\n      selectable: true,\r\n      labelShiftY: -10,\r\n      labelShiftX: -10      \r\n    });\r\n  });\r\n\r\n  // 3) Add main pin markers (each with customInfo)\r\n  const pinPath = \"M16,0 C9.163,0 3.747,5.416 3.747,12.253 C3.747,20.556 16,32 16,32 C16,32 28.253,20.556 28.253,12.253 C28.253,5.416 22.837,0 16,0 Z M16,16 C12.686,16 10,13.314 10,10 C10,6.686 12.686,4 16,4 C19.314,4 22,6.686 22,10 C22,13.314 19.314,16 16,16 Z\";\r\n  const pinColors = [\"#FF5252\",\"#FFB300\",\"#00BCD4\",\"#8BC34A\",\"#7E57C2\",\"#FF7043\"];\r\n  cityMarkers.forEach((c, idx) => {\r\n    map.dataProvider.images.push({\r\n      id: \"pin_\"+c.id,\r\n      latitude: c.lat,\r\n      longitude: c.lon,\r\n      title: c.title,\r\n      customInfo: c.info,\r\n      svgPath: svg,\r\n      color: pinColors[idx % pinColors.length],\r\n      scale: 0.9,\r\n      selectable: true,\r\n      // keep label blank to prevent clutter; the tooltip and modal show info\r\n      label: \"\",\r\n      labelShiftY: 8,\r\n      _isMainPin: true\r\n    });\r\n  });\r\n\r\n  // final: validate so amCharts updates balloons with customInfo\r\n  map.validateData();\r\n\r\n  // Attach pulse behind pins after a short delay (map rendering complete)\r\n  setTimeout(() => attachPulseToPins(), 350);\r\n});\r\n\r\n/* ---------- Click handler: open modal for area or image ---------- */\r\nmap.addListener(\"clickMapObject\", function(event) {\r\n  const obj = event.mapObject;\r\n  if(!obj) return;\r\n  const title = obj.title || obj.label || obj.id || \"Region / Place\";\r\n  const desc = obj.customInfo || obj.description || \"No details available.\";\r\n  openModal(title, desc);\r\n});\r\n\r\n/* ---------- Modal logic ---------- */\r\nconst modalBackdrop = document.getElementById(\"modalBackdrop\");\r\nconst modalTitle = document.getElementById(\"modalTitle\");\r\nconst modalBody  = document.getElementById(\"modalBody\");\r\nconst modalClose = document.getElementById(\"modalClose\");\r\n\r\nfunction openModal(title, html){\r\n    Swal.fire({\r\n    title: \'<strong>\'+title+\'</strong>\',\r\n    html: html,\r\n    icon: \'info\',\r\n    showCloseButton: true,\r\n    showCancelButton: true,\r\n    focusConfirm: false,\r\n    confirmButtonText: \'OK\',\r\n    cancelButtonText: \'Cancel\'\r\n  });\r\n  /*\r\n  modalTitle.textContent = title;\r\n  modalBody.innerHTML = `<p style=\"margin:0 0 10px 0; color:#374151\">${html}</p>\r\n    <div style=\"margin-top:10px; font-size:13px; color:var(--muted)\">\r\n      <strong>Actions:</strong>\r\n      <ul style=\"margin:6px 0 0 18px; padding:0; color:var(--muted)\">\r\n        <li><button onclick=\"zoomToThis(\'${title}\')\" style=\"border:0;background:#eef6ff;padding:6px 8px;border-radius:6px;cursor:pointer\">Zoom to this region</button></li>\r\n      </ul>\r\n    </div>`;\r\n  modalBackdrop.classList.add(\"show\");\r\n  modalBackdrop.setAttribute(\"aria-hidden\",\"false\");*/\r\n}\r\n\r\nfunction closeModal(){ modalBackdrop.classList.remove(\"show\"); modalBackdrop.setAttribute(\"aria-hidden\",\"true\"); }\r\nmodalClose.addEventListener(\"click\", closeModal);\r\nmodalBackdrop.addEventListener(\"click\", function(e){ if(e.target === modalBackdrop) closeModal(); });\r\n\r\n/* ---------- Pulse attach (SVG) ---------- */\r\nfunction attachPulseToPins(){\r\n  try {\r\n    const svgRoot = document.querySelector(\"#mapdiv svg\");\r\n    if(!svgRoot) return;\r\n    // Remove existing pulses to avoid duplicates\r\n    svgRoot.querySelectorAll(\"circle.pulse\").forEach(c => c.remove());\r\n    // amCharts creates g.amcharts-image groups for images/pins\r\n    const imageGroups = svgRoot.querySelectorAll(\"g.amcharts-image\");\r\n    imageGroups.forEach(g => {\r\n      const path = g.querySelector(\"path\");\r\n      if(!path) return;\r\n      // compute bounding box & center\r\n      const bbox = g.getBBox();\r\n      const cx = bbox.x + bbox.width/2;\r\n      const cy = bbox.y + bbox.height/2;\r\n      const svgns = \"http://www.w3.org/2000/svg\";\r\n      const circle = document.createElementNS(svgns, \"circle\");\r\n      circle.setAttribute(\"cx\", cx);\r\n      circle.setAttribute(\"cy\", cy);\r\n      circle.setAttribute(\"r\", 6);\r\n      const fill = path.getAttribute(\"fill\") || path.style.fill || \"#FF5252\";\r\n      circle.setAttribute(\"fill\", fill);\r\n      circle.setAttribute(\"opacity\", \"0.6\");\r\n      circle.classList.add(\"pulse\");\r\n      const parent = g.parentNode;\r\n      if(parent) parent.insertBefore(circle, g);\r\n\r\n      // pointer cursor & simple hover effect\r\n      g.style.cursor = \"pointer\";\r\n      g.addEventListener(\"mouseenter\", ()=> {\r\n        try {\r\n          // scale visually by modifying transform attribute\r\n          const current = g.getAttribute(\"transform\") || \"\";\r\n          g.setAttribute(\"data-prev-transform\", current);\r\n          g.setAttribute(\"transform\", current + \" scale(1.06)\");\r\n        } catch(e){}\r\n      });\r\n      g.addEventListener(\"mouseleave\", ()=> {\r\n        const prev = g.getAttribute(\"data-prev-transform\") || \"\";\r\n        if(prev === \"\") g.removeAttribute(\"transform\");\r\n        else g.setAttribute(\"transform\", prev);\r\n      });\r\n    });\r\n\r\n    // re-attach pulses on render (map pan/zoom causes re-render)\r\n    map.addListener(\"rendered\", function(){\r\n      const svgRoot2 = document.querySelector(\"#mapdiv svg\");\r\n      if(!svgRoot2) return;\r\n      svgRoot2.querySelectorAll(\"circle.pulse\").forEach(c => c.remove());\r\n      setTimeout(()=> attachPulseToPins(), 120);\r\n    });\r\n\r\n  } catch(err){\r\n    console.warn(\"Pulse attach error:\", err);\r\n  }\r\n}\r\n\r\n/* ---------- helper: zoomToThis by matching title in regions or cityMarkers ---------- */\r\nfunction zoomToThis(name){\r\n  // try find lat/lon from regions or cityMarkers\r\n  const r = regions.find(x => x.title === name || x.key === name) || cityMarkers.find(x => x.title === name || x.id === name);\r\n  if(r){\r\n    map.zoomToLongLat(6, r.lon, r.lat); // zoom level 6 is example\r\n    closeModal();\r\n  } else {\r\n    alert(\"Coordinate for \'\" + name + \"\' not found for zoom.\");\r\n  }\r\n}\r\n\r\n/* ---------- fallback zoom helper ---------- */\r\nfunction zoomToLatLon(lat, lon, level){\r\n  try { map.zoomToLongLat(level || 6, lon, lat); } catch(e){ console.warn(e); }\r\n}\r\n                       </script>', '.section-wrapper {\r\n    padding: 3rem 0;\r\n}\r\n\r\n.container {\r\n    max-width: 1200px;\r\n    margin: 0 auto;\r\n    padding: 0 1rem;\r\n}', '// Section JavaScript\r\nconsole.log(\'Section loaded\');', '{}', 1, 1, '2025-12-01 09:51:12', '2025-12-02 04:46:19'),
