ign: center;\r\n            color: #666;\r\n            font-size: 14px;\r\n            margin-top: 10px;\r\n        }\r\n\r\n        .message {\r\n            padding: 15px;\r\n            border-radius: 10px;\r\n            margin-bottom: 20px;\r\n            display: none;\r\n            animation: slideDown 0.3s ease;\r\n        }\r\n\r\n        @keyframes slideDown {\r\n            from {\r\n                opacity: 0;\r\n                transform: translateY(-10px);\r\n            }\r\n            to {\r\n                opacity: 1;\r\n                transform: translateY(0);\r\n            }\r\n        }\r\n\r\n        .message.success {\r\n            background: #d4edda;\r\n            color: #155724;\r\n            border: 1px solid #c3e6cb;\r\n        }\r\n\r\n        .message.error {\r\n            background: #f8d7da;\r\n            color: #721c24;\r\n            border: 1px solid #f5c6cb;\r\n        }\r\n\r\n        .required {\r\n            color: #e74c3c;\r\n        }\r\n\r\n        @media (max-width: 600px) {\r\n            .container {\r\n                padding: 25px;\r\n            }\r\n\r\n            .header h1 {\r\n                font-size: 26px;\r\n            }\r\n\r\n            .star {\r\n                font-size: 35px;\r\n            }\r\n\r\n            .button-group {\r\n                flex-direction: column;\r\n            }\r\n        }', ' // Anti-spam variables\r\n        let formStartTime = Date.now();\r\n        let lastSubmitTime = localStorage.getItem(\'lastReviewSubmit\') || 0;\r\n        const COOLDOWN_PERIOD = 300000; // 5 minutes in milliseconds\r\n        \r\n        // Track user interactions (real users interact more naturally)\r\n        let interactionScore = 0;\r\n        let fieldOrder = [];\r\n        let mouseMovements = 0;\r\n        let keystrokes = 0;\r\n\r\n        // Star rating functionality\r\n        const stars = document.querySelectorAll(\'.star\');\r\n        const ratingInput = document.getElementById(\'rating\');\r\n        const form = document.getElementById(\'reviewForm\');\r\n        const messageBox = document.getElementById(\'messageBox\');\r\n        const submitBtn = document.getElementById(\'submitBtn\');\r\n        let selectedRating = 0;\r\n\r\n        // Initialize anti-spam measures\r\n        window.onload = function() {\r\n            generateToken();\r\n            document.getElementById(\'timestamp\').value = Date.now();\r\n         //   checkCooldown();\r\n            trackUserBehavior();\r\n        };\r\n\r\n        // Generate unique form token\r\n        function generateToken() {\r\n            const token = Math.random().toString(36).substring(2) + Date.now().toString(36);\r\n            document.getElementById(\'formToken\').value = token;\r\n        }\r\n\r\n        \r\n\r\n        // Track natural user behavior (bots don\'t move mouse or type naturally)\r\n        function trackUserBehavior() {\r\n            // Track mouse movements\r\n            document.addEventListener(\'mousemove\', function() {\r\n                mouseMovements++;\r\n                if (mouseMovements <= 50) interactionScore += 1;\r\n            });\r\n\r\n            // Track keystrokes\r\n            document.addEventListener(\'keyup\', function() {\r\n                keystrokes++;\r\n                if (keystrokes <= 100) interactionScore += 2;\r\n            });\r\n\r\n            // Track field focus order (humans follow logical order)\r\n            const fields = [\'name_user\', \'email_user\', \'message\'];\r\n            fields.forEach((fieldId, index) => {\r\n                document.getElementById(fieldId).addEventListener(\'focus\', function() {\r\n                    if (!fieldOrder.includes(fieldId)) {\r\n                        fieldOrder.push(fieldId);\r\n                        interactionScore += 5;\r\n                    }\r\n                });\r\n            });\r\n\r\n            // Track star interactions\r\n            stars.forEach(star => {\r\n                star.addEventListener(\'click\', function() {\r\n                    interactionScore += 10;\r\n                });\r\n            });\r\n        }\r\n\r\n        // Cancel button - return to website\r\n        function goBack() {\r\n            if (confirm(\'Are you sure you want to cancel? Your review will not be saved.\')) {\r\n                // Change this URL to your website\'s home page or previous page\r\n                window.location.href = \'/reviews\'; // or use: history.back();\r\n            }\r\n        }\r\n\r\n        // Star rating functionality\r\n        stars.forEach(star => {\r\n            star.addEventListener(\'click\', function() {\r\n                selectedRating = this.getAttribute(\'data-rating\');\r\n                ratingInput.value = selectedRating;\r\n                updateStars();\r\n            });\r\n\r\n            star.addEventListener(\'mouseenter\', function() {\r\n                const hoverRating = this.getAttribute(\'data-rating\');\r\n                highlightStars(hoverRating);\r\n            });\r\n        });\r\n\r\n        document.getElementById(\'starRating\').addEventListener(\'mouseleave\', function() {\r\n            updateStars();\r\n        });\r\n\r\n        function highlightStars(rating) {\r\n            stars.forEach(star => {\r\n                if (star.getAttribute(\'data-rating\') <= rating) {\r\n                    star.classList.add(\'active\');\r\n                } else {\r\n                    star.classList.remove(\'active\');\r\n                }\r\n            });\r\n        }\r\n\r\n        function updateStars() {\r\n            highlightStars(selectedRating);\r\n        }\r\n\r\n        // Form submission with anti-spam checks\r\n        form.addEventListener(\'submit\', async function(e) {\r\n            e.preventDefault();\r\n\r\n            // Validate rating\r\n            if (selectedRating === 0) {\r\n                showMessage(\'Please select a rating\', \'error\');\r\n                return;\r\n            }\r\n\r\n            // Check honeypot\r\n            if (document.querySelector(\'input[name=\"website\"]\').value !== \'\') {\r\n                showMessage(\'Spam detected. Submission blocked.\', \'error\');\r\n                return;\r\n            }\r\n\r\n            // Calculate time spent on form\r\n            const timeSpent = (Date.now() - formStartTime) / 1000;\r\n            document.getElementById(\'timeSpent\').value = timeSpent;\r\n            \r\n            // Update interaction data\r\n            document.getElementById(\'interactionScore\').value = interactionScore;\r\n            document.getElementById(\'fieldOrder\').value = fieldOrder.join(\',\');\r\n\r\n            // Minimum interaction check (bots have low scores)\r\n            if (interactionScore < 15) {\r\n                showMessage(\'Please interact with the form naturally before submitting.\', \'error\');\r\n                return;\r\n            }\r\n\r\n            // Time check\r\n            if (timeSpent < 3) {\r\n                showMessage(\'Please take your time to fill out the form properly.\', \'error\');\r\n                return;\r\n            }\r\n\r\n            submitBtn.disabled = true;\r\n            submitBtn.textContent = \'Submitting...\';\r\n\r\n            const formData = new FormData(form);\r\n\r\n            try {\r\n                const response = await fetch(\'submit_review.php\', {\r\n                    method: \'POST\',\r\n                    body: formData\r\n                });\r\n\r\n                const result = await response.json();\r\n\r\n\r\n                if (result.success) {\r\n                    showMessage(\'Thank you! Your review has been submitted and is pending approval.\', \'success\');\r\n                    form.reset();\r\n                    selectedRating = 0;\r\n                    updateStars();\r\n                    generateToken();\r\n                    \r\n                    // Reset interaction tracking\r\n                    interactionScore = 0;\r\n                    fieldOrder = [];\r\n                    mouseMovements = 0;\r\n                    keystrokes = 0;\r\n                    \r\n                    // Set cooldown\r\n                    localStorage.setItem(\'lastReviewSubmit\', Date.now());\r\n                    lastSubmitTime = Date.now();\r\n                   // checkCooldown();\r\n                    \r\n                    // Optionally redirect after success\r\n                    setTimeout(() => {\r\n                        // window.location.href = \'index.html\';\r\n                    }, 3000);\r\n                } else {\r\n                    showMessage(result.message || \'An error occurred. Please try again.\', \'error\');\r\n                }\r\n            } catch (error) {\r\n                showMessage(\'Connection error. Please try again later.\', \'error\');\r\n            } finally {\r\n                if (!submitBtn.disabled) {\r\n                    submitBtn.textContent = \'Submit Review\';\r\n                }\r\n            }\r\n        });\r\n\r\n        function showMessage(text, type) {\r\n            messageBox.textContent = text;\r\n            messageBox.className = \'message \' + type;\r\n            messageBox.style.display = \'block\';\r\n            \r\n            setTimeout(() => {\r\n                messageBox.style.display = \'none\';\r\n            }, 5000);\r\n        }', '/uploads/templates/1764510826_Capture d’écran 2025-11-30 145320.png', 0, 'active', 1, '2025-11-29 12:38:52', '2025-12-02 03:57:39');
