(1, 'Media Grid Template', 'media-grid-template', 'Grid layout for displaying media files with thumbnails', 'media', '<div class=\"media-item col-md-4\">\r\n  <div class=\"media-thumbnail\">\r\n    <img src=\"{{ $item.url }}\" alt=\"{{ $item.original_filename }}\" style=\"width:100%\" />\r\n  </div>\r\n  <div class=\"media-info\">\r\n    <h4>{{ $item.original_filename }}</h4>\r\n    <a href=\"{{ $item.url }}\" class=\"btn-download\" download>Download</a>\r\n  </div>\r\n</div>', '.media-item {\r\n  background: #fff;\r\n  border: 1px solid #e5e7eb;\r\n  border-radius: 8px;\r\n  padding: 1rem;\r\n  transition: all 0.3s;\r\n}\r\n.media-item:hover {\r\n  box-shadow: 0 4px 6px rgba(0,0,0,0.1);\r\n  transform: translateY(-2px);\r\n}\r\n.media-thumbnail {\r\n  width: 100%;\r\n  height: 200px;\r\n  overflow: hidden;\r\n  border-radius: 4px;\r\n  margin-bottom: 0.75rem;\r\n}\r\n.media-thumbnail img {\r\n  width: 100%;\r\n  height: 100%;\r\n  object-fit: cover;\r\n}\r\n.media-info h4 {\r\n  font-size: 1rem;\r\n  font-weight: 600;\r\n  margin-bottom: 0.5rem;\r\n  color: #1f2937;\r\n}\r\n.media-type, .media-size {\r\n  font-size: 0.875rem;\r\n  color: #6b7280;\r\n  margin-bottom: 0.25rem;\r\n}\r\n.btn-download {\r\n  display: inline-block;\r\n  margin-top: 0.5rem;\r\n  padding: 0.5rem 1rem;\r\n  background: #3b82f6;\r\n  color: white;\r\n  text-decoration: none;\r\n  border-radius: 4px;\r\n  font-size: 0.875rem;\r\n}\r\n.btn-download:hover {\r\n  background: #2563eb;\r\n}', '', '[{\"key\":\"url\",\"label\":\"URL\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"original_filename\",\"label\":\"Filename\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"file_type\",\"label\":\"File Type\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"file_size\",\"label\":\"File Size\",\"type\":\"text\",\"default\":\"\"}]', 'url,original_filename', NULL, 1, 'active', '2025-11-12 12:17:05', '2025-11-12 14:36:30'),
(2, 'Blog Post Card', 'blog-post-card', 'Modern card layout for blog posts with image and excerpt', 'post', '<article class=\"post-card col-md-4\">\r\n  <div class=\"post-image\">\r\n    <img src=\"{{ $item.featured_image }}\" alt=\"{{ $item.title }}\" style=\"width: 120px\" />\r\n  </div>\r\n  <div class=\"post-content\">\r\n    <div class=\"post-meta\">\r\n      <span class=\"post-date\">{{ $item.created_at }}</span>\r\n      <span class=\"post-author\">By {{ $item.author }}</span>\r\n    </div>\r\n    <h3 class=\"post-title\">{{ $item.title }}</h3>\r\n    <p class=\"post-excerpt\">{{ $item.excerpt }}</p>\r\n    <a href=\"/post/{{ $item.slug }}\" class=\"read-more\">Read More →</a>\r\n  </div>\r\n</article>', '.post-card {\r\n  background: white;\r\n  border-radius: 12px;\r\n  overflow: hidden;\r\n  box-shadow: 0 2px 8px rgba(0,0,0,0.08);\r\n  transition: all 0.3s;\r\n  height: 100%;\r\n  display: flex;\r\n  flex-direction: column;\r\n}\r\n.post-card:hover {\r\n  box-shadow: 0 8px 16px rgba(0,0,0,0.12);\r\n  transform: translateY(-4px);\r\n}\r\n.post-image {\r\n  width: 100%;\r\n  height: 240px;\r\n  overflow: hidden;\r\n}\r\n.post-image img {\r\n  width: 100%;\r\n  height: 100%;\r\n  object-fit: cover;\r\n  transition: transform 0.3s;\r\n}\r\n.post-card:hover .post-image img {\r\n  transform: scale(1.05);\r\n}\r\n.post-content {\r\n  padding: 1.5rem;\r\n  flex: 1;\r\n  display: flex;\r\n  flex-direction: column;\r\n}\r\n.post-meta {\r\n  display: flex;\r\n  gap: 1rem;\r\n  font-size: 0.875rem;\r\n  color: #6b7280;\r\n  margin-bottom: 0.75rem;\r\n}\r\n.post-title {\r\n  font-size: 1.25rem;\r\n  font-weight: 700;\r\n  color: #111827;\r\n  margin-bottom: 0.75rem;\r\n  line-height: 1.4;\r\n}\r\n.post-excerpt {\r\n  color: #4b5563;\r\n  line-height: 1.6;\r\n  margin-bottom: 1rem;\r\n  flex: 1;\r\n}\r\n.read-more {\r\n  color: #3b82f6;\r\n  font-weight: 600;\r\n  text-decoration: none;\r\n  display: inline-flex;\r\n  align-items: center;\r\n  transition: color 0.2s;\r\n}\r\n.read-more:hover {\r\n  color: #2563eb;\r\n}', '', '[{\"key\":\"featured_image\",\"label\":\"Featured Image\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"title\",\"label\":\"Title\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"excerpt\",\"label\":\"Excerpt\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"slug\",\"label\":\"Slug\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"created_at\",\"label\":\"Date\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"author\",\"label\":\"Author\",\"type\":\"text\",\"default\":\"\"}]', 'featured_image,title,excerpt,slug,created_at', NULL, 1, 'active', '2025-11-12 12:17:05', '2025-11-12 14:34:44'),
(4, 'Page List Item', 'page-list-item', 'Simple list item for displaying pages', 'page', '<div class=\"page-item\">\n  <div class=\"page-icon\">📄</div>\n  <div class=\"page-info\">\n    <h4 class=\"page-title\">{{ $item.title }}</h4>\n    <p class=\"page-description\">{{ $item.meta_description }}</p>\n    <a href=\"/{{ $item.slug }}\" class=\"page-link\">View Page →</a>\n  </div>\n</div>', '.page-item {\n  display: flex;\n  gap: 1rem;\n  padding: 1.5rem;\n  background: white;\n  border: 1px solid #e5e7eb;\n  border-radius: 8px;\n  transition: all 0.3s;\n}\n.page-item:hover {\n  border-color: #3b82f6;\n  box-shadow: 0 4px 8px rgba(59, 130, 246, 0.1);\n}\n.page-icon {\n  font-size: 2rem;\n}\n.page-info {\n  flex: 1;\n}\n.page-title {\n  font-size: 1.125rem;\n  font-weight: 600;\n  color: #111827;\n  margin-bottom: 0.5rem;\n}\n.page-description {\n  color: #6b7280;\n  font-size: 0.95rem;\n  margin-bottom: 0.75rem;\n  line-height: 1.5;\n}\n.page-link {\n  color: #3b82f6;\n  text-decoration: none;\n  font-weight: 500;\n  font-size: 0.95rem;\n}\n.page-link:hover {\n  color: #2563eb;\n}', NULL, '[{\"key\":\"title\",\"label\":\"Title\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"meta_description\",\"label\":\"Description\",\"type\":\"text\",\"default\":\"\"},{\"key\":\"slug\",\"label\":\"Slug\",\"type\":\"text\",\"default\":\"\"}]', 'title,meta_description,slug', NULL, 1, 'active', '2025-11-12 12:17:05', '2025-11-12 12:17:05'),
